// Js脚本来源：https://unpkg.com/docsify-count@1.1.0/dist/countable.min.js
var defaultOptions=
{
    countable:!0,
    position:"top",
    margin:"10px",
    float:"right",
    fontsize:"0.9em",
    color:"rgb(90,90,90)",
    language:"chinese",
    isExpected:!0
};

function plugin(t,n){
    if(!defaultOptions.countable)return;
    let o;
    t.beforeEach
    (
        function(t)
        {
            return o=t.match(/([\u4e00-\u9fa5]+?|[a-zA-Z0-9]+)/g).length,t
        }
    ),
    t.afterEach
    (
        function(t,n)
        {
            let i=o+" words",
            e=Math.ceil(o/400)+" min";
            "chinese"===defaultOptions.language && 
            (i=o+" 字",e=Math.ceil(o/400)+" 分钟"),
                n(
                `
                    ${"bottom"===defaultOptions.position?t:""}
                    <div style="margin-${defaultOptions.position?"bottom":"top"}: ${defaultOptions.margin};">
                        <span style="float: ${"right"===defaultOptions.float?"right":"left"};font-size: ${defaultOptions.fontsize};color:${defaultOptions.color};">
                            ${i}
                            ${defaultOptions.isExpected?`&nbsp; | &nbsp;${e}`:""}
                        </span>
                        <div style="clear: both">
                        </div>
                    </div>
                    ${"bottom"!==defaultOptions.position?t:""}
                `
            )
        }
    )
}
window.$docsify.count=Object.assign(defaultOptions,window.$docsify.count),
window.$docsify.plugins=[].concat(plugin,window.$docsify.plugins);